    if(onOff === 'on') {
      if(UILockerRequireCount.current === 0) {
        setIsUILockerOn(true);
      }
      UILockerRequireCount.current++;
    } else if(onOff === 'off') {
      UILockerRequireCount.current--;
      if(UILockerRequireCount.current === 0) {
        setIsUILockerOn(true);
      }
    }
  }

